mapper.delete:
DELETE FROM keyword
WHERE
    user_id = #{userId}
    AND keyword = #{keyword}
    AND count = #{count}
    AND kw_code = #{kwCode}

mapper.update:
UPDATE keyword
SET
    count = #{newCount}
WHERE
    user_id = #{userId}
    AND keyword = #{keyword}
    AND count = #{count}
    AND kw_code = #{kwCode}
