mapper.select:
SELECT CHAT_CODE
FROM REPORT
WHERE REPORT_CODE = #{reportCode}

SELECT * FROM ( SELECT ROWNUM AS no, x.* FROM ( SELECT R.REPORT_CODE, R.CHAT_CODE, R.USER_ID, R.CT_ID, R.REASON, AS REASON_LABEL, R.REASON_DETAIL, R.STATUS, R.REG_DT, R.MOD_DT, R.TARGET_USER_ID, R.TARGET_MESSAGE, R.TARGET_SEND_DT FROM REPORT R
AND ( UPPER(R.USER_ID) LIKE #{kw} OR UPPER(R.CT_ID) LIKE #{kw} )
AND R.STATUS = #{status}
AND R.CHAT_CODE = #{chatCode}
AND R.REASON = #{reason}
AND R.USER_ID = #{userId}
AND R.CT_ID = #{ctId}
ORDER BY R.REG_DT DESC, R.REPORT_CODE DESC ) x WHERE ROWNUM <= (#{pageNo} * #{pageSize}) ) WHERE no > ((#{pageNo} - 1) * #{pageSize})

mapper.update:
UPDATE REPORT
CHAT_CODE = #{chatCode}, USER_ID = #{userId}, CT_ID = #{ctId}, REASON = #{reason}, STATUS = #{status}, TARGET_USER_ID = #{targetUserId}, TARGET_MESSAGE = #{targetMessage}, TARGET_SEND_DT = #{targetSendDt,jdbcType=TIMESTAMP}, MOD_DT = SYSDATE
WHERE REPORT_CODE = #{reportCode}

mapper.select:
SELECT count(*) totalCnt 
FROM REPORT
WHERE user_id = #{userId}
