mapper.select:



SELECT *
FROM (
  SELECT ROWNUM AS no, x.*
  FROM (
    SELECT
      R.REPORT_CODE, 
      R.CHAT_CODE, 
      R.USER_ID, 
      R.CT_ID,
      R.REASON, 
      R.STATUS, 
      R.REG_DT
    FROM REPORT R



        AND ( UPPER(R.USER_ID) LIKE #{kw}
           OR UPPER(R.CT_ID)   LIKE #{kw} )




        AND R.STATUS = #{status}


        AND R.CHAT_CODE = #{chatCode}


        AND R.REASON = #{reason}


        AND R.USER_ID = #{userId}


        AND R.CT_ID = #{ctId}


    ORDER BY R.REG_DT DESC, R.REPORT_CODE DESC
  ) x
  WHERE ROWNUM <= (#{pageNo} * #{pageSize}) 
)
WHERE no > ((#{pageNo} - 1) * #{pageSize})

mapper.update:
UPDATE REPORT
CHAT_CODE = #{chatCode}, USER_ID = #{userId}, CT_ID = #{ctId}, REASON = #{reason}, STATUS = #{status},
WHERE REPORT_CODE = #{reportCode}

UPDATE REPORT
 SET STATUS = #{status}
WHERE REPORT_CODE = #{reportCode}
